import React, {useState} from 'react';
import GenerateBlog from './components/GenerateBlog';
import BlogList from './components/BlogList';
import Pricing from './components/Pricing';

export default function App(){
  const [blogs, setBlogs] = useState([]);
  const [selected, setSelected] = useState(null);

  return (
    <div>
      <h2>AI SEO Blog Generator (v6) â€” Payments Integrated</h2>
      <GenerateBlog onCreate={(b)=>{ setBlogs([b, ...blogs]); setSelected(b); }} />
      <Pricing userId={'demo-user'} />
      <div style={{display:'flex', gap:20, marginTop:20}}>
        <div style={{flex:1}}><BlogList blogs={blogs} onSelect={(b)=>setSelected(b)} /></div>
        <div style={{flex:2}}>{selected ? <div><h3>Selected Blog</h3><pre style={{whiteSpace:'pre-wrap'}}>{JSON.stringify(selected, null, 2)}</pre></div> : <div>Select a blog</div>}</div>
      </div>
    </div>
  );
}
